      'X-APICloud-AppKey': `${sha1(`${appID}UZ${appKey}UZ${date}`)}.${date}`
    };
  };
  return {
    async getAllTodos () {
      try {
        return handleRequest(await request.get(
          `/${className}`,
          { headers: getHeaders() }
        ));
      } catch (e) {
        console.log('网络错误.');
        return null;
      }
    },
    async addTodo (todo) {
      try {
        return handleRequest(await request.post(
          `/${className}`,
          todo,
          { headers: getHeaders() }
        ));
      } catch (e) {
        console.log('网络错误.');
        return null;
      }
    },
    async updateTodo (id, todo) {
      try {
        return handleRequest(await request.put(
          `/${className}/${id}`,
          todo,
          { headers: getHeaders() }
        ));
      } catch (e) {
        console.log('网络错误.');
        return null;
      }
    },
    async deleteTodo (id) {
      try {
        return handleRequest(await request.delete(
          `/${className}/${id}`,
          { headers: getHeaders() }
        ));
      } catch (e) {
        console.log('网络错误.');
        return null;
      }
    },
    async deleteCompleted (ids) { // 根据ID批量操作（删除）
      try {
        const requests = ids.map(id => {
          return {
            'method': 'DELETE',
            'path': `/mcm/api/${className}/${id}`
          };
        });
        return handleRequest(await request.post(
          '/batch',
          { requests },
          { headers: getHeaders() }
        ));
      } catch (e) {
        console.log('网络错误.');
        return null;
      }
    }
  };
};


