import Vuex from 'vuex';
import state from './state';
import mutations from './mutations';
import * as getters from './getters';
import * as actions from './actions';
import createLogger from 'vuex/dist/logger';

const debug = process.env.NODE_ENV === 'development';

export default () => {
  const store = new Vuex.Store({
    state,
    mutations,
    getters,
    actions,
    plugins: debug ? [createLogger()] : [],
    strict: debug
  });

  // 添加热更替的代码
  if (module.hot) {
    module.hot.accept([
      './state',
      './mutations',
      './getters',
      './actions'
    ], () => {
      const newState = require('./state').default;
      const newMutations = require('./mutations').default;
      const newGetters = require('./getters').default;
      const newActions = require('./actions').default;

      console.log(store.hotUpdate);
      store.hotUpdate({
        state: newState,
        mutations: newMutations,
        getters: newGetters,
        actions: newActions
      });
    });
  }

  return store;
}

export const SET_COUNT = 'SET_COUNT';
export const SET_TODOS = 'SET_TODOS';
export const SET_LOADING_VISIBLE = 'SET_LOADING_VISIBLE';
export const DELETE_TODO = 'DELETE_TODO';
export const UPDATE_TODO = 'UPDATE_TODO';
export const ADD_TODO = 'ADD_TODO';
export const DELETE_COMPLETED = 'DELETE_COMPLETED';

import * as types from './mutation-types';

const mutations = {
  [types.SET_COUNT] (state, count) {
    state.count = count;
  },
  [types.SET_TODOS] (state, todos) {
    state.todos = todos;
  },
  [types.DELETE_TODO] (state, id) {
    let index = state.todos.findIndex(todo => todo.id === id);
    state.todos.splice(index, 1);
  },
  [types.UPDATE_TODO] (state, { id, flag }) {
    let index = state.todos.findIndex(todo => todo.id === id);
    state.todos[index].completed = flag;
  },
  [types.ADD_TODO] (state, todo) {
    state.todos.unshift(todo);
  },
  [types.SET_LOADING_VISIBLE] (state, flag) {
    state.loadingVisible = flag;
  },
  [types.DELETE_COMPLETED] (state, ids) {
    state.todos = state.todos.filter(todo => {
      return ids.every(id => id !== todo.id);
    });
  }
};

export default mutations;

const state = {
  count: 1,
  todos: [],
  loadingVisible: false
};

export default state;

